import React, {components, useState} from 'react';

// import { Container } from './styles';

const TableHead = () => {
    return (
    <thead>
        <tr>
            <th>Autores</th>
            <th>Livros</th>
            <th>Pre√ßos</th>
            <th>Remover</th>
        </tr>
    </thead>
    );
}

const TableBody = props => {
    const linhas = props.autores.map( (linha, index) => {
      return (
        <tr key={index}>
          <td>{linha.nome}</td>
          <td>{linha.livro}</td>
          <td>{linha.preco}</td>
          <td><button>Remover</button></td>
        </tr>
      )
    })
    return(
      <tbody>
        {linhas}
      </tbody>
    )
}

//passar props tem que passar por parametro na funcao
export default function Tabela(props) {
  const {autores} = props
  // console.log(autores);
  return (
    <div>
      <table>
       <TableHead />
        <TableBody autores={autores} />
      </table>
    </div>

  )
}
